import React from 'react'

function AppTodoList({ SetMyList, myList }) {
    console.log(myList)

    const handelClick = (index) => {
        const newList = [...myList]
        newList[index].complete = true
        SetMyList(newList)
    }

    const removeObject = (index) => {
        const newList = [...myList]
        newList.splice(index, 1)
        SetMyList(newList)
    }

    return (
    <div>

        {myList.map((myList, index) => (
            <div key={index} > 
                <button onClick={() => {handelClick(index)}} style={{ 
                    textDecoration: myList[index].complete ? 'line-through' : ''
                    }}>{myList.value}</button>
                <button onClick={() => {removeObject(index)}}>x</button>
            </div>
        ))}
    </div>
  )
}

export default AppTodoList
